INSERT INTO `categories` (`id`, `name`, `avatar`, `description`, `status`, `created_at`, `updated_at`) VALUES
(1, 'Điện thoại', '1686930556-samsung-galaxy-20-fe_3_.jpg', '<p>Điện thoại l&agrave; một thiết bị di động được sử dụng để li&ecirc;n lạc, gửi tin nhắn v&agrave; truy cập internet. Với t&iacute;nh năng gọi điện, nhắn tin v&agrave; c&aacute;c ứng dụng đa phương tiện, điện thoại l&agrave; c&ocirc;ng cụ kh&ocirc;ng thể thiếu trong cuộc sống hiện đại. C&oacute; nhiều h&atilde;ng điện thoại như iPhone, Samsung, v&agrave; Xiaomi, mỗi h&atilde;ng mang đến c&aacute;c d&ograve;ng sản phẩm với t&iacute;nh năng v&agrave; thiết kế đa dạng để đ&aacute;p ứng nhu cầu của người d&ugrave;ng</p>\r\n', 1, '2023-06-16 15:49:16', '2023-06-20 01:21:36'),
(2, 'Tablet', '1687006519-tablet.jpg', '<p>Tablet l&agrave; một thiết bị di động c&oacute; m&agrave;n h&igrave;nh cảm ứng lớn, th&ocirc;ng thường từ 7 đến 13 inch, v&agrave; được thiết kế để cung cấp một trải nghiệm tương tự như m&aacute;y t&iacute;nh x&aacute;ch tay nhưng với k&iacute;ch thước nhỏ gọn hơn.&nbsp;Tablet c&oacute; thể được sử dụng để truy cập internet, gửi v&agrave; nhận email, xem phim, đọc s&aacute;ch điện tử, lướt web, chơi game v&agrave; nghe nhạc. Ngo&agrave;i ra, tablet cũng th&iacute;ch hợp để xem video, xem ảnh, duyệt ảnh v&agrave; thậm ch&iacute; l&agrave;m việc với c&aacute;c ứng dụng văn ph&ograve;ng</p>\r\n', 1, '2023-06-17 12:55:19', '2023-06-20 01:13:51'),
(3, 'Laptop', '1686932095-macbook-air-m1-2020-gray-600x600.jpg', '<p>Laptop&nbsp;c&ograve;n được gọi l&agrave; m&aacute;y t&iacute;nh x&aacute;ch tay, l&agrave; một thiết bị di động được thiết kế để cung cấp trải nghiệm tương tự như m&aacute;y t&iacute;nh để b&agrave;n nhưng với t&iacute;nh di động cao hơn.&nbsp;Laptop cho ph&eacute;p người d&ugrave;ng thực hiện nhiều c&ocirc;ng việc như lướt web, gửi v&agrave; nhận email, xem phim, nghe nhạc, chơi game v&agrave; l&agrave;m việc với c&aacute;c ứng dụng văn ph&ograve;ng.&nbsp;Laptop c&oacute; nhiều k&iacute;ch thước v&agrave; ph&acirc;n loại kh&aacute;c nhau, từ nhỏ gọn v&agrave; nhẹ để dễ d&agrave;ng mang theo, đến c&aacute;c m&aacute;y t&iacute;nh x&aacute;ch tay mạnh mẽ d&agrave;nh cho c&ocirc;ng việc đ&ograve;i hỏi hiệu suất cao. Hệ điều h&agrave;nh phổ biến cho laptop l&agrave; Windows của Microsoft, MacOS của Apple v&agrave; Linux</p>\r\n', 1, '2023-06-16 16:07:14', '2023-06-20 01:16:04'),
(6, 'Tai nghe', '1686931495-bluetooth-true-wireless-ava-ds201a-wb-021021-090449-600x600.jpg', '<p>Tai nghe l&agrave; một thiết bị &acirc;m thanh c&aacute; nh&acirc;n được đeo v&agrave;o tai để nghe nhạc hoặc thực hiện cuộc gọi. C&oacute; c&aacute;c loại tai nghe như in-ear (được ch&egrave;n v&agrave;o tai), on-ear (đ&egrave; l&ecirc;n tai) v&agrave; over-ear (che k&iacute;n tai). Tai nghe cũng c&oacute; thể kh&ocirc;ng d&acirc;y để cung cấp t&iacute;nh di động v&agrave; tự do di chuyển. N&oacute; l&agrave; một phụ kiện phổ biến cho điện thoại di động v&agrave; c&aacute;c thiết bị di động kh&aacute;c.</p>\r\n', 1, '2023-06-16 16:04:55', '2023-06-20 01:17:30'),
(8, 'Đồng hồ thông minh', '1686931798-OIP.jpg', '<p>Đồng hồ th&ocirc;ng minh l&agrave; một thiết bị đeo tay th&ocirc;ng minh, kết hợp c&aacute;c t&iacute;nh năng của đồng hồ truyền thống với khả năng kết nối v&agrave; th&ocirc;ng minh. N&oacute; c&oacute; thể hiển thị thời gian, th&ocirc;ng b&aacute;o cuộc gọi, tin nhắn, gi&aacute;m s&aacute;t sức khỏe v&agrave; t&iacute;ch hợp c&aacute;c ứng dụng kh&aacute;c. Đồng hồ th&ocirc;ng minh gi&uacute;p người d&ugrave;ng tiện lợi trong việc theo d&otilde;i v&agrave; điều khiển th&ocirc;ng tin từ c&aacute;c thiết bị di động.</p>\r\n', 1, '2023-06-16 16:09:58', '2023-06-20 01:18:12'),
(9, 'Sạc dự phòng', '1686931968-image-removebg-preview-1.jpg', '<p>Sạc sự ph&ograve;ng, c&ograve;n được gọi l&agrave; pin dự ph&ograve;ng, l&agrave; một thiết bị di động c&oacute; khả năng lưu trữ năng lượng v&agrave; sử dụng để sạc lại c&aacute;c thiết bị di động như điện thoại di động, m&aacute;y t&iacute;nh bảng hoặc tai nghe kh&ocirc;ng d&acirc;y. Sạc sự ph&ograve;ng gi&uacute;p bạn duy tr&igrave; năng lượng v&agrave; tiếp tục sử dụng thiết bị của m&igrave;nh khi kh&ocirc;ng c&oacute; nguồn điện gần đ&acirc;y. Với sạc sự ph&ograve;ng, bạn c&oacute; thể sạc lại thiết bị của m&igrave;nh mọi l&uacute;c, mọi nơi.</p>\r\n', 1, '2023-06-16 16:12:48', '2023-06-20 01:23:59'),
(35, 'Loa', '1687006709-loa2.jpg', '<p>Loa l&agrave; một thiết bị &acirc;m thanh d&ugrave;ng để t&aacute;i tạo &acirc;m thanh v&agrave; ph&aacute;t ra &acirc;m thanh từ c&aacute;c nguồn &acirc;m như điện thoại, m&aacute;y t&iacute;nh, hoặc thiết bị ph&aacute;t nhạc kh&aacute;c. Loa c&oacute; thể được kết nối qua d&acirc;y hoặc kh&ocirc;ng d&acirc;y v&agrave; c&oacute; nhiều loại v&agrave; k&iacute;ch thước kh&aacute;c nhau, bao gồm loa di động, loa hifi v&agrave; loa vi t&iacute;nh. Loa gi&uacute;p tăng cường trải nghiệm &acirc;m thanh, từ việc nghe nhạc, xem phim cho đến thực hiện c&aacute;c cuộc gọi điện thoại với &acirc;m thanh r&otilde; r&agrave;ng v&agrave; sống động.</p>\r\n', 1, '2023-06-17 12:58:29', '2023-06-20 01:24:59');

